definition.script:
pipeline {
    agent any
        stages {
            stage('checkout') {
                steps {
                        sh 'echo "checking path"'
                        sh 'pwd'
                }
        }
            stage('switch') {
                steps {
                        sh 'mkdir -p /home/ubuntu/folder'
                        dir("/home/ubuntu/folder") {
                            sh 'touch file'
                        }
                }
            }
    }
}
